html:
{% set settings = frappe.get_doc('Inventory Settings') %}
<style>
    * {
        margin: 0;
        padding: 0;
        box-sizing: border-box;
    }

    @page {
        size: A4;
        margin: 8mm;
    }

    body {
        font-family: {{ "'Arial', 'Tahoma', sans-serif" if frappe.local.lang == 'ar' else "'Arial', 'Helvetica', sans-serif" }};
        font-size: 8.5pt;
        color: #000;
        line-height: 1.3;
        direction: {{ 'rtl' if frappe.local.lang == 'ar' else 'ltr' }};
    }

    .bon-livraison {
        width: 100%;
        max-width: 100%;
    }

    /* Header Section */
    .header-box {
        border: 2px solid #000;
        padding: 8px;
        margin-bottom: 10px;
    }

    .header-grid {
        display: table;
        width: 100%;
        border-collapse: collapse;
    }

    .header-left {
        display: table-cell;
        width: 58%;
        vertical-align: top;
        padding-{{ 'left' if frappe.local.lang == 'ar' else 'right' }}: 10px;
        border-{{ 'left' if frappe.local.lang == 'ar' else 'right' }}: 2px solid #000;
    }

    .header-right {
        display: table-cell;
        width: 42%;
        vertical-align: top;
        padding-{{ 'right' if frappe.local.lang == 'ar' else 'left' }}: 10px;
        text-align: center;
    }

    .company-name {
        font-size: 14pt;
        font-weight: bold;
        text-transform: uppercase;
        margin-bottom: 4px;
        color: #000;
    }

    .legal-form {
        font-size: 9pt;
        font-weight: bold;
        margin-bottom: 6px;
    }

    .company-info {
        font-size: 7.5pt;
        line-height: 1.4;
        margin-bottom: 6px;
    }

    .legal-info {
        font-size: 7pt;
        line-height: 1.3;
        border-top: 1px solid #ccc;
        padding-top: 4px;
        margin-top: 4px;
    }

    .legal-info div {
        margin-bottom: 1px;
    }

    .doc-title {
        font-size: 18pt;
        font-weight: bold;
        text-transform: uppercase;
        margin-bottom: 10px;
        padding: 6px;
        background: #000;
        color: #fff;
        text-align: center;
    }

    .doc-number {
        font-size: 10pt;
        font-weight: bold;
        margin-bottom: 6px;
    }

    .doc-date {
        font-size: 9pt;
        margin-bottom: 4px;
    }

    /* Client Section */
    .client-section {
        border: 2px solid #000;
        padding: 8px;
        margin-bottom: 10px;
    }

    .client-grid {
        display: table;
        width: 100%;
    }

    .client-col {
        display: table-cell;
        width: 50%;
        vertical-align: top;
        padding: 4px;
    }

    .client-col:first-child {
        border-{{ 'left' if frappe.local.lang == 'ar' else 'right' }}: 1px solid #ccc;
        padding-{{ 'left' if frappe.local.lang == 'ar' else 'right' }}: 10px;
    }

    .client-col:last-child {
        padding-{{ 'right' if frappe.local.lang == 'ar' else 'left' }}: 10px;
    }

    .section-label {
        font-size: 8pt;
        font-weight: bold;
        text-transform: uppercase;
        margin-bottom: 6px;
        padding-bottom: 2px;
        border-bottom: 1px solid #000;
    }

    .client-name {
        font-size: 10pt;
        font-weight: bold;
        margin-bottom: 4px;
    }

    .client-details {
        font-size: 8pt;
        line-height: 1.4;
    }

    /* Items Table */
    .items-table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 10px;
        border: 2px solid #000;
        table-layout: fixed;
    }

    .items-table thead {
        background: #000;
        color: #fff;
    }

    .items-table th {
        padding: 6px 3px;
        text-align: center;
        font-weight: bold;
        font-size: 7.5pt;
        text-transform: uppercase;
        border: 1px solid #000;
        word-wrap: break-word;
    }

    .items-table td {
        padding: 5px 3px;
        border: 1px solid #000;
        font-size: 8pt;
        vertical-align: top;
        word-wrap: break-word;
        overflow: hidden;
    }

    .items-table tbody tr:nth-child(even) {
        background-color: #f5f5f5;
    }

    .item-description {
        font-weight: bold;
        font-size: 8pt;
    }

    .item-code {
        font-size: 7pt;
        color: #555;
        font-style: italic;
    }

    .text-center {
        text-align: center !important;
    }

    .text-right {
        text-align: {{ 'left' if frappe.local.lang == 'ar' else 'right' }} !important;
    }

    .text-left {
        text-align: {{ 'right' if frappe.local.lang == 'ar' else 'left' }} !important;
    }

    /* Total Section */
    .total-section {
        margin-top: 10px;
        margin-bottom: 15px;
    }

    .total-box {
        float: {{ 'left' if frappe.local.lang == 'ar' else 'right' }};
        width: 40%;
        border: 2px solid #000;
    }

    .total-row {
        display: table;
        width: 100%;
        border-bottom: 1px solid #000;
    }

    .total-row:last-child {
        border-bottom: none;
        background: #000;
        color: #fff;
    }

    .total-label {
        display: table-cell;
        padding: 6px 8px;
        font-weight: bold;
        font-size: 9pt;
        text-transform: uppercase;
        width: 55%;
    }

    .total-value {
        display: table-cell;
        padding: 6px 8px;
        text-align: {{ 'left' if frappe.local.lang == 'ar' else 'right' }};
        font-weight: bold;
        font-size: 10pt;
        width: 45%;
    }

    .clearfix::after {
        content: "";
        display: table;
        clear: both;
    }

    /* Observations */
    .observations {
        border: 2px solid #000;
        padding: 8px;
        margin-bottom: 10px;
        min-height: 50px;
        clear: both;
    }

    .observations-title {
        font-weight: bold;
        font-size: 8pt;
        text-transform: uppercase;
        margin-bottom: 6px;
        border-bottom: 1px solid #000;
        padding-bottom: 2px;
    }

    .observations-text {
        font-size: 8pt;
        line-height: 1.4;
    }

    /* Signatures */
    .signatures {
        display: table;
        width: 100%;
        margin-top: 20px;
        border: 2px solid #000;
    }

    .signature-cell {
        display: table-cell;
        width: 33.33%;
        padding: 10px 8px;
        text-align: center;
        border-{{ 'left' if frappe.local.lang == 'ar' else 'right' }}: 1px solid #000;
        vertical-align: bottom;
    }

    .signature-cell:last-child {
        border-{{ 'left' if frappe.local.lang == 'ar' else 'right' }}: none;
    }

    .signature-label {
        font-weight: bold;
        font-size: 8pt;
        text-transform: uppercase;
        margin-bottom: 40px;
        display: block;
    }

    .signature-line {
        border-top: 1px solid #000;
        padding-top: 4px;
        font-size: 7pt;
        color: #666;
    }

    /* Footer */
    .footer {
        margin-top: 15px;
        padding-top: 8px;
        border-top: 2px solid #000;
        text-align: center;
        font-size: 6.5pt;
        color: #666;
    }

    @media print {
        body {
            -webkit-print-color-adjust: exact;
            print-color-adjust: exact;
        }

        .bon-livraison {
            padding: 0;
        }

        .page-break {
            page-break-after: always;
        }
    }
</style>

<div class="bon-livraison">
    <!-- Header -->
    <div class="header-box">
        <div class="header-grid">
            <div class="header-left">
                <div class="company-name">{{ settings.company_name or doc.company or ("اسم الشركة" if frappe.local.lang == 'ar' else "NOM DE L'ENTREPRISE") }}</div>
                {% if settings.legal_form %}
                <div class="legal-form">{{ settings.legal_form }}</div>
                {% endif %}
                <div class="company-info">
                    {% if settings.company_address %}
                    <div><strong>{{ "العنوان" if frappe.local.lang == 'ar' else "Adresse" }}:</strong> {{ settings.company_address }}</div>
                    {% endif %}
                    {% if settings.company_phone %}
                    <div><strong>{{ "هاتف" if frappe.local.lang == 'ar' else "Tél" }}:</strong> {{ settings.company_phone }}</div>
                    {% endif %}
                    {% if settings.company_fax %}
                    <div><strong>{{ "فاكس" if frappe.local.lang == 'ar' else "Fax" }}:</strong> {{ settings.company_fax }}</div>
                    {% endif %}
                    {% if settings.company_email %}
                    <div><strong>{{ "البريد الإلكتروني" if frappe.local.lang == 'ar' else "Email" }}:</strong> {{ settings.company_email }}</div>
                    {% endif %}
                    {% if settings.company_website %}
                    <div><strong>{{ "الموقع" if frappe.local.lang == 'ar' else "Web" }}:</strong> {{ settings.company_website }}</div>
                    {% endif %}
                </div>
                <div class="legal-info">
                    {% if settings.rc_number %}
                    <div><strong>RC:</strong> {{ settings.rc_number }}</div>
                    {% endif %}
                    {% if settings.nif_number %}
                    <div><strong>NIF:</strong> {{ settings.nif_number }}</div>
                    {% endif %}
                    {% if settings.nis_number %}
                    <div><strong>NIS:</strong> {{ settings.nis_number }}</div>
                    {% endif %}
                    {% if settings.article_imposition %}
                    <div><strong>{{ "المادة" if frappe.local.lang == 'ar' else "Art." }}:</strong> {{ settings.article_imposition }}</div>
                    {% endif %}
                    {% if settings.capital_social %}
                    <div><strong>{{ "رأس المال" if frappe.local.lang == 'ar' else "Capital" }}:</strong> {{ frappe.format_value(settings.capital_social, {'fieldtype': 'Currency'}) }} DZD</div>
                    {% endif %}
                </div>
            </div>
            <div class="header-right">
                <div class="doc-title">{{ "إذن تسليم" if frappe.local.lang == 'ar' else "BON DE LIVRAISON" }}</div>
                <div class="doc-number">{{ "رقم" if frappe.local.lang == 'ar' else "N°" }} {{ doc.name }}</div>
                <div class="doc-date"><strong>{{ "التاريخ" if frappe.local.lang == 'ar' else "Date" }}:</strong> {{ frappe.utils.formatdate(doc.delivery_date, 'dd/MM/yyyy') }}</div>
                {% if doc.posting_time %}
                <div class="doc-date"><strong>{{ "الوقت" if frappe.local.lang == 'ar' else "Heure" }}:</strong> {{ doc.posting_time }}</div>
                {% endif %}
            </div>
        </div>
    </div>

    <!-- Client Information -->
    <div class="client-section">
        <div class="client-grid">
            <div class="client-col">
                <div class="section-label">{{ "العميل" if frappe.local.lang == 'ar' else "Client" }}</div>
                <div class="client-name">{{ doc.customer_name or doc.customer }}</div>
                {% if doc.customer_address_display %}
                <div class="client-details">
                    {{ doc.customer_address_display|replace("\n", "<br>")|safe }}
                </div>
                {% endif %}
            </div>
            <div class="client-col">
                <div class="section-label">{{ "عنوان التسليم" if frappe.local.lang == 'ar' else "Adresse de Livraison" }}</div>
                {% if doc.shipping_address_display %}
                <div class="client-details">
                    {{ doc.shipping_address_display|replace("\n", "<br>")|safe }}
                </div>
                {% else %}
                <div class="client-details">
                    {{ doc.customer_address_display|replace("\n", "<br>")|safe if doc.customer_address_display else ("نفس العنوان" if frappe.local.lang == 'ar' else "Même adresse") }}
                </div>
                {% endif %}
                {% if doc.sales_order %}
                <div style="margin-top: 6px; font-size: 8pt;">
                    <strong>{{ "رقم الطلب" if frappe.local.lang == 'ar' else "Commande N°" }}:</strong> {{ doc.sales_order }}
                </div>
                {% endif %}
            </div>
        </div>
    </div>

    <!-- Items Table -->
    <table class="items-table">
        <thead>
            <tr>
                <th style="width: 5%;">{{ "رقم" if frappe.local.lang == 'ar' else "N°" }}</th>
                <th style="width: 55%;">{{ "التعيين" if frappe.local.lang == 'ar' else "Désignation" }}</th>
                <th style="width: 10%;">{{ "الكمية" if frappe.local.lang == 'ar' else "Qté" }}</th>
                <th style="width: 10%;">{{ "الوحدة" if frappe.local.lang == 'ar' else "Unité" }}</th>
                <th style="width: 10%;">{{ "السعر" if frappe.local.lang == 'ar' else "P.U." }} (DZD)</th>
                <th style="width: 10%;">{{ "المبلغ" if frappe.local.lang == 'ar' else "Montant" }} (DZD)</th>
            </tr>
        </thead>
        <tbody>
            {% for item in doc.items %}
            <tr>
                <td class="text-center">{{ loop.index }}</td>
                <td>
                    <div class="item-description">{{ item.item_name }}</div>
                    {% if item.description and item.description != item.item_name %}
                    <div style="font-size: 7pt; color: #555; margin-top: 1px;">{{ item.description[:80] }}{% if item.description|length > 80 %}...{% endif %}</div>
                    {% endif %}
                </td>
                <td class="text-center"><strong>{{ "%.2f"|format(item.quantity) }}</strong></td>
                <td class="text-center">{{ item.uom }}</td>
                <td class="text-right">{{ "%.2f"|format(item.rate) }}</td>
                <td class="text-right"><strong>{{ "%.2f"|format(item.amount) }}</strong></td>
            </tr>
            {% endfor %}
        </tbody>
    </table>

    <!-- Total Section -->
    <div class="total-section clearfix">
        <div class="total-box">
            <div class="total-row">
                <div class="total-label">{{ "المجموع بدون رسوم" if frappe.local.lang == 'ar' else "Total HT" }}</div>
                <div class="total-value">{{ "%.2f"|format(doc.total_amount) }}</div>
            </div>
            {% if doc.total_taxes_and_charges %}
            <div class="total-row">
                <div class="total-label">{{ "الرسوم" if frappe.local.lang == 'ar' else "TVA" }}</div>
                <div class="total-value">{{ "%.2f"|format(doc.total_taxes_and_charges) }}</div>
            </div>
            {% endif %}
            <div class="total-row">
                <div class="total-label">{{ "المجموع الإجمالي" if frappe.local.lang == 'ar' else "Total TTC" }}</div>
                <div class="total-value">{{ "%.2f"|format(doc.grand_total or doc.total_amount) }}</div>
            </div>
        </div>
    </div>

    <!-- Observations -->
    <div class="observations">
        <div class="observations-title">{{ "ملاحظات" if frappe.local.lang == 'ar' else "Observations / Remarques" }}</div>
        <div class="observations-text">
            {% if doc.remarks %}
                {{ doc.remarks }}
            {% else %}
                &nbsp;
            {% endif %}
        </div>
    </div>

    <!-- Conditions -->
    {% if doc.terms %}
    <div class="observations" style="min-height: auto;">
        <div class="observations-title">{{ "الشروط العامة" if frappe.local.lang == 'ar' else "Conditions Générales" }}</div>
        <div class="observations-text">
            {{ doc.terms|safe }}
        </div>
    </div>
    {% endif %}

    <!-- Signatures -->
    <div class="signatures">
        <div class="signature-cell">
            <span class="signature-label">{{ "المسلم" if frappe.local.lang == 'ar' else "Le Livreur" }}</span>
            <div class="signature-line">{{ "الاسم والتوقيع" if frappe.local.lang == 'ar' else "Nom et Signature" }}</div>
        </div>
        <div class="signature-cell">
            <span class="signature-label">{{ "المسؤول" if frappe.local.lang == 'ar' else "Le Responsable" }}</span>
            <div class="signature-line">{{ "الاسم والتوقيع" if frappe.local.lang == 'ar' else "Nom et Signature" }}</div>
        </div>
        <div class="signature-cell">
            <span class="signature-label">{{ "العميل" if frappe.local.lang == 'ar' else "Le Client" }}</span>
            <div class="signature-line">{{ "الختم والتوقيع" if frappe.local.lang == 'ar' else "Cachet et Signature" }}</div>
        </div>
    </div>

    <!-- Footer -->
    <div class="footer">
        <div>{{ "تم إنشاء المستند في" if frappe.local.lang == 'ar' else "Document généré le" }} {{ frappe.utils.now_datetime().strftime('%d/%m/%Y à %H:%M') if frappe.local.lang != 'ar' else frappe.utils.now_datetime().strftime('%d/%m/%Y %H:%M') }}</div>
        <div style="margin-top: 2px;">{{ "هذا المستند هو إذن تسليم رسمي - يجب الاحتفاظ به" if frappe.local.lang == 'ar' else "Ce document est un bon de livraison officiel - À conserver" }}</div>
    </div>
</div>
